import "core-js/modules/es.array.push.js";
import p5 from 'p5'; // Ensure 'p5' is correctly imported

export default {
  name: 'Standartcaptcha',
  data() {
    return {
      fonts: [require('@/assets/Barriecito/Barriecito-Regular.ttf'), require('@/assets/DM_Serif_Display/DMSerifDisplay-Italic.ttf'), require('@/assets/Roboto/Roboto-Regular.ttf')] // Array of font file paths
    };
  },
  mounted() {
    const container = this.$refs.sketchWindow;
    new p5(sketch => {
      let letters = []; // Array to store letters
      let circleX, circleY, circleRadius; // Variables for circle position and radius

      sketch.setup = () => {
        sketch.createCanvas(500, 300).parent(container);
        sketch.background(250);
        sketch.textAlign(sketch.CENTER, sketch.CENTER); // Align text to center

        // Generate random circle position and radius
        circleX = sketch.width / 2;
        circleY = sketch.height / 2;
        circleRadius = sketch.random(100, 300);

        // Generate 5 random letters
        const numLetters = 5;
        const letterSpacing = sketch.width / (numLetters + 1); // Calculate spacing between letters
        for (let i = 0; i < numLetters; i++) {
          const randomCharCode = sketch.floor(sketch.random(97, 123)); // Random ASCII code for lowercase letters
          const randomLetter = String.fromCharCode(randomCharCode); // Convert ASCII code to letter
          const x = (i + 1) * letterSpacing; // Calculate x position based on index and spacing
          const y = sketch.height / 2 + sketch.random(-sketch.height * 0.15, sketch.height * 0.1); // Randomly offset vertically
          const fontIndex = Math.floor(Math.random() * this.fonts.length); // Randomly select a font
          const selectedFont = sketch.loadFont(this.fonts[fontIndex]); // Load the selected font
          const fontSize = 20 + 5 * i; // Increase font size by 5 for each letter
          letters.push({
            letter: randomLetter,
            x,
            y,
            font: selectedFont,
            fontSize
          });
        }
      };
      sketch.draw = () => {
        sketch.background(250); // Clear background

        // Draw black circle
        sketch.fill(0); // Set fill color to black
        sketch.circle(circleX, circleY, circleRadius); // Draw circle with random position and radius

        // Draw letters
        for (let i = 0; i < letters.length; i++) {
          const {
            letter,
            x,
            y,
            font,
            fontSize
          } = letters[i];
          sketch.textFont(font); // Set font for the current letter
          sketch.textSize(fontSize); // Set font size for the current letter
          sketch.text(letter, x, y);
        }
      };
    });
  },
  methods: {
    redirectToError() {
      // Redirect to /error route
      this.$router.push('/error');
    }
  }
};
